import React, { useState, useEffect } from "react";
import Nav from "../Nav.jsx";
import hybridIndex from "./hybridIndex.js"; // Adjusted import
import Filter from "./Filter.jsx";
import Dropdown from "./dropDown.jsx";
import ProductGrid from "./productGrid.jsx";
import "../../Styles/searchPage.css";
import products from "../../Data/data.js";

products.forEach((product) => hybridIndex.addProduct(product));

const ProductSearch = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [dropdown, setDropdown] = useState([]);
  const [dropdownStyle, setDropdownStyle] = useState("2px solid #e5e7eb");
  const [pricefilter, setPriceFilter] = useState(10000);
  const [cart,setCart] = useState([]);

  const handleClear = () => {
    setQuery("");
    const defaultProducts = products.filter((data) => data.default === "yes");
    setResults(defaultProducts);
    setDropdown([]);
    setDropdownStyle("2px solid #e5e7eb");
    setPriceFilter(10000);
  };

  const searchForPhone = () =>{
    setResults(hybridIndex.search("phone")); 
    setQuery("phone");
  }
  const searchForCamera = () =>{
    setResults(hybridIndex.search("camera")); 
    setQuery("camera");
  }
  const searchForSpeaker = () =>{
    setResults(hybridIndex.search("speaker")); 
    setQuery("speaker");
  }
  const searchForAudio = () =>{
    setResults(hybridIndex.search("audio")); 
    setQuery("audio");
  }
  const searchForTablet = () =>{
    setResults(hybridIndex.search("tablet")); 
    setQuery("tablet");
  }
  const searchForLaptop = () =>{
    setResults(hybridIndex.search("laptop")); 
    setQuery("laptop");
  }
  

  const priceHightoLow = () => {
    const sortedResults = [...results].sort((a, b) => b.price - a.price);
    setResults(sortedResults);
  };

  const priceLowtoHigh = () => {
    const sortedResults = [...results].sort((a, b) => a.price - b.price);
    setResults(sortedResults);
  };

  const settingPriceRange = (e) => {
    e.preventDefault();
    const searchResults = hybridIndex
      .search(query)
      .filter((product) => product.price < pricefilter);
    setResults(searchResults);
  };

  const addToCart = (product) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const searchResults = hybridIndex.search(query);
    setResults(searchResults);
    setDropdown([]);
    setDropdownStyle("2px solid #e5e7eb");
  };

  const searchValue = (e) => {
    const value = e.target.value;
    setQuery(value);
    if (value) {
      const searchResults = hybridIndex.search(value);
      setDropdown(searchResults);
      setDropdownStyle(searchResults.length > 0 ? "none" : "2px solid #e5e7eb");
    } else {
      setDropdown([]);
    }
  };

  useEffect(() => {
    const defaultProducts = products.filter((data) => data.default === "yes");
    setResults(defaultProducts);
  }, []);

  return (
    <React.Fragment>
      <Nav setCart={cart}/>
      <div className="search-container">
        <Filter
          priceHightoLow={priceHightoLow}
          priceLowtoHigh={priceLowtoHigh}
          pricefilter={pricefilter}
          setPriceFilter={setPriceFilter}
          settingPriceRange={settingPriceRange}
          handleClear={handleClear}
          searchForPhone={searchForPhone}
          searchForAudio={searchForAudio}
          searchForCamera={searchForCamera}
          searchForSpeaker={searchForSpeaker}
          searchForTablet={searchForTablet}
          searchForLaptop={searchForLaptop}
        />
        <div className="search-form">
          <form onSubmit={handleSubmit}>
            <div className="input-wrapper">
              <input
                type="text"
                value={query}
                onChange={searchValue}
                placeholder="Search for a product..."
                style={{ borderBottom: dropdownStyle }}
              />
              <button type="submit">Go</button>
            </div>
            {dropdown.length > 0 && (
              <Dropdown
                dropdown={dropdown}
                setQuery={setQuery}
                setResults={setResults}
                setDropdown={setDropdown}
                setDropdownStyle={setDropdownStyle}
              />
            )}
          </form>
          <ProductGrid 
            results={results}
            addToCart={addToCart}
           />
        </div>
      </div>
    </React.Fragment>
  );
};

export default ProductSearch;
