import React from "react";

const ProductGrid = ({ results, addToCart }) => {
  return (
    <React.Fragment>
      <div className="results-grid">
        {results.length > 0 ? (
          results.map((product) => (
            <div key={product.id} className="result-item">
              <img src={product.image} alt="product" className="images" />
              <h3>{product.name}</h3>
              <p>Price: {product.price} ₹</p>
              <p>{product.description}</p>
              <div className="cartAndBuy" style={{ display: "flex", gap: "10px" }}>
                <button className="buyButton" onClick={() => alert(`Buying ${product.name} for ₹${product.price}`)}>
                  Buy Now
                </button>
                <button className="cartButton" onClick={() => addToCart(product)}>
                  Add to Cart
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="no-results">No products found</div>
        )}
      </div>
    </React.Fragment>
  );
};

export default ProductGrid;
