import React, { useState } from 'react';

export default function AddToCart() {
  const products = [
    {
      id: 10,
      name: "Microsoft Surface Pro 7",
      keywords: ["tablet", "microsoft", "surface", "microsoft surface pro 7"],
      image: "https://m.media-amazon.com/images/I/41XQTF-mheL.SY300_SX300_QL70_FMwebp.jpg",
      price: 749,
    },
    {
      id: 11,
      name: "Apple iPad Pro",
      keywords: ["tablet", "apple", "ipad", "ipad pro"],
      image: "https://m.media-amazon.com/images/I/81pY5hvXfEL._AC_SX522_.jpg",
      price: 999,
    },
    // Add more items as needed
  ];

  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center' }}>Products</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {products.map((product) => (
          <div key={product.id} style={{
            border: '1px solid #ddd', borderRadius: '8px', width: '220px', padding: '10px', textAlign: 'center',
            boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
          }}>
            <img src={product.image} alt={product.name} style={{ width: '100%', height: '150px', objectFit: 'cover', borderRadius: '8px' }} />
            <h3 style={{ fontSize: '1.1rem', margin: '10px 0' }}>{product.name}</h3>
            <p style={{ color: '#555' }}>${product.price}</p>
            <button onClick={() => addToCart(product)} style={{
              backgroundColor: '#007bff', color: '#fff', border: 'none', padding: '10px', borderRadius: '5px', cursor: 'pointer'
            }}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      <h2 style={{ marginTop: '30px', textAlign: 'center' }}>Cart</h2>
      <div style={{ marginTop: '20px' }}>
        {cart.length > 0 ? (
          cart.map((item) => (
            <div key={item.id} style={{
              display: 'flex', alignItems: 'center', margin: '10px 0', padding: '10px', border: '1px solid #ddd', borderRadius: '8px'
            }}>
              <img src={item.image} alt={item.name} style={{ width: '60px', height: '60px', objectFit: 'cover', borderRadius: '8px' }} />
              <div style={{ marginLeft: '15px', flex: '1' }}>
                <h4 style={{ margin: '0 0 5px' }}>{item.name}</h4>
                <p style={{ margin: '0', color: '#555' }}>${item.price}</p>
              </div>
              <button onClick={() => removeFromCart(item.id)} style={{
                backgroundColor: '#dc3545', color: '#fff', border: 'none', padding: '8px', borderRadius: '5px', cursor: 'pointer'
              }}>
                Remove
              </button>
            </div>
          ))
        ) : (
          <p style={{ textAlign: 'center', color: '#777' }}>Your cart is empty.</p>
        )}
      </div>
    </div>
  );
}
