import React, { useState, useEffect } from "react";
import Nav from "../Nav.jsx";
import hybridIndex from "./hybridIndex.js";
import Filter from "./Filter.jsx";
import Dropdown from "../shop/dropDown.jsx";
import ProductGrid from "../shop/productGrid.jsx";
import "../../Styles/searchPage.css";
import products from "../../Data/data.js";

// Initialize the hybridIndex with products
products.forEach((product) => hybridIndex.addProduct(product));

const ProductSearch = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [dropdown, setDropdown] = useState([]);
  const [dropdownStyle, setDropdownStyle] = useState("2px solid #e5e7eb");
  const [priceFilter, setPriceFilter] = useState(10000);
  const [cart, setCart] = useState([]);
  const [accountuser,SetAccountUser]  = useState([]);

  const handleClear = () => {
    setQuery("");
    const defaultProducts = products.filter((data) => data.default === "yes");
    setResults(defaultProducts);
    setDropdown([]);
    setDropdownStyle("2px solid #e5e7eb");
    setPriceFilter(10000);
  };

  const searchForCategory = (category) => {
    setResults(hybridIndex.search(category));
    setQuery(category);
  };

  const priceHighToLow = () => {
    const sortedResults = [...results].sort((a, b) => b.price - a.price);
    setResults(sortedResults);
  };

  const priceLowToHigh = () => {
    const sortedResults = [...results].sort((a, b) => a.price - b.price);
    setResults(sortedResults);
  };

  const settingPriceRange = (e) => {
    e.preventDefault();
    const searchResults = hybridIndex
      .search(query)
      .filter((product) => product.price < priceFilter);
    setResults(searchResults);
  };

  const addToCart = (product) => {
    console.log(accountuser._id);
    if(accountuser){
       fetch(`http://localhost:9000/users/${accountuser.id}/cart`,{
          method:"POST",
          headers:{
            "Content-Type": "application/json",
          },
          body:JSON.stringify({
            Product:product.name,
            Image:product.iamge,
            Price:product.price
          }),
       }).then(
        alert("Item added")
       ).then(()=>{
         fetch(`http://localhost:9000/users/cart/${accountuser.id}`,{
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
         })
         .then((resp)=>{ 
            let res = resp.json();
          setCart([...res, {quantity: 1 }]);
         })
       })
    }
   else{
     alert('Login please');
     return
   }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const searchResults = hybridIndex.search(query);
    setResults(searchResults);
    setDropdown([]);
    setDropdownStyle("2px solid #e5e7eb");
  };

  const searchValue = (e) => {
    const value = e.target.value;
    setQuery(value);
    if (value) {
      const searchResults = hybridIndex.search(value);
      setDropdown(searchResults);
      setDropdownStyle(searchResults.length > 0 ? "none" : "2px solid #e5e7eb");
    } else {
      setDropdown([]);
    }
  };

  useEffect(() => {
    const defaultProducts = products.filter((data) => data.default === "yes");
    setResults(defaultProducts);
  }, []);

  return (
    <React.Fragment>
      <Nav cart={cart} setCart={setCart} user={accountuser} setUser={SetAccountUser} />
      <div className="search-container">
        <Filter
          priceHightoLow={priceHighToLow}
          priceLowtoHigh={priceLowToHigh}
          priceFilter={priceFilter}
          setPriceFilter={setPriceFilter}
          settingPriceRange={settingPriceRange}
          handleClear={handleClear}
          searchForPhone={() => searchForCategory("phone")}
          searchForAudio={() => searchForCategory("audio")}
          searchForCamera={() => searchForCategory("camera")}
          searchForSpeaker={() => searchForCategory("speaker")}
          searchForTablet={() => searchForCategory("tablet")}
          searchForLaptop={() => searchForCategory("laptop")}
        />
        <div className="search-form">
          <form onSubmit={handleSubmit}>
            <div className="input-wrapper">
              <input
                type="text"
                value={query}
                onChange={searchValue}
                placeholder="Search for a product..."
                style={{ borderBottom: dropdownStyle }}
              />
              <button type="submit">Go</button>
            </div>
            {dropdown.length > 0 && (
              <Dropdown
                dropdown={dropdown}
                setQuery={setQuery}
                setResults={setResults}
                setDropdown={setDropdown}
                setDropdownStyle={setDropdownStyle}
              />
            )}
          </form>
          <ProductGrid
            results={results}
            addToCart={addToCart}
          />
        </div>
      </div>
    </React.Fragment>
  );
};

export default ProductSearch;
