import React from "react";
import "../../Styles/buy.css"; // Custom CSS styles

const Checkout = () => {
  // Hardcoded product data
  const product = {
    name: "Wireless Earbuds",
    image: "https://via.placeholder.com/250", // Replace with an actual image URL
    description: "High-quality wireless earbuds with noise cancellation and long battery life.",
    price: 1999,
    relatedProducts: [
      { id: 1, name: "Bluetooth Headphones", price: 2999, image: "https://via.placeholder.com/150" },
      { id: 2, name: "Portable Speaker", price: 2499, image: "https://via.placeholder.com/150" },
      { id: 3, name: "Wireless Charger", price: 1499, image: "https://via.placeholder.com/150" },
    ],
  };

  return (
    <div className="buying-page">
      <div className="product-details">
        <img src={product.image} alt={product.name} className="product-image" />
        <div className="product-info">
          <h2>{product.name}</h2>
          <p className="product-description">{product.description}</p>
          <p className="product-price">₹{product.price}</p>
          <p className="offer">Special Offer: Get 10% off on your first purchase!</p>
          <div className="quantity-selector">
            <label>Quantity:</label>
            <input type="number" defaultValue="1" min="1" className="quantity-input" />
          </div>
          <p className="total-price">Total Price: ₹{product.price}</p>
          <button className="buy-now-button">Buy Now</button>
        </div>
      </div>
      <div className="similar-products">
        <h3>Related Products</h3>
        <div className="similar-product-list">
          {product.relatedProducts.map((relatedProduct) => (
            <div key={relatedProduct.id} className="related-product-item">
              <img src={relatedProduct.image} alt={relatedProduct.name} />
              <p>{relatedProduct.name}</p>
              <p>₹{relatedProduct.price}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Checkout;
