import React, { useState } from 'react';
import '../Styles/cart.css';

export default function AddToCart() {
  const products = [
    {
      id: 10,
      name: "Microsoft Surface Pro 7",
      keywords: ["tablet", "microsoft", "surface", "microsoft surface pro 7"],
      image: "https://m.media-amazon.com/images/I/41XQTF-mheL.SY300_SX300_QL70_FMwebp.jpg",
      price: 749,
    },
    {
      id: 11,
      name: "Apple iPad Pro",
      keywords: ["tablet", "apple", "ipad", "ipad pro"],
      image: "https://m.media-amazon.com/images/I/81pY5hvXfEL._AC_SX522_.jpg",
      price: 999,
    },
    // Add more products as needed
  ];

  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter((item) => item.id !== id));
  };

  const calculateTotal = () => {
    return cart.reduce((acc, item) => acc + item.price * item.quantity, 0).toFixed(2);
  };

  const buyNow = (product) => {
    alert(`Buying ${product.name} for $${product.price * product.quantity}`);
  };

  const buyAll = () => {
    alert(`Buying all items in the cart for $${calculateTotal()}`);
    setCart([]); // Clears the cart
  };

  const updateQuantity = (id, quantity) => {
    setCart(cart.map(item =>
      item.id === id ? { ...item, quantity: Math.max(1, parseInt(quantity) || 1) } : item
    ));
  };

  return (
    <div className="cart-container">
      <h2>Products</h2>
      <div className="products-container">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            <p>${product.price}</p>
            <button onClick={() => addToCart(product)} className="add-to-cart-btn">
              Add to Cart
            </button>
            <button onClick={() => buyNow({ ...product, quantity: 1 })} className="buy-now-btn">
              Buy Now
            </button>
          </div>
        ))}
      </div>

      <h2>Cart</h2>
      <div className="cart-content">
        {cart.length > 0 ? (
          <div>
            {cart.map((item) => (
              <div key={item.id} className="cart-item">
                <img src={item.image} alt={item.name} className="cart-item-image" />
                <div className="cart-item-details">
                  <h4>{item.name}</h4>
                  <p>Price: ${item.price}</p>
                  <div className="quantity-container">
                    <label>Quantity:</label>
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => updateQuantity(item.id, e.target.value)}
                      className="quantity-input"
                    />
                  </div>
                  <p>Total: ${(item.price * item.quantity).toFixed(2)}</p>
                </div>
                <button onClick={() => buyNow(item)} className="buy-now-cart-btn">
                  Buy Now
                </button>
                <button onClick={() => removeFromCart(item.id)} className="remove-btn">
                  Remove
                </button>
              </div>
            ))}
            <div className="total-price">
              <h3>Total Price: ${calculateTotal()}</h3>
              <button onClick={buyAll} className="buy-all-btn">
                Buy All
              </button>
            </div>
          </div>
        ) : (
          <p className="empty-cart">Your cart is empty.</p>
        )}
      </div>
    </div>
  );
}
