import React, { useState } from 'react';

export default function AddToCart() {
  const products = [
    {
      id: 10,
      name: "Microsoft Surface Pro 7",
      keywords: ["tablet", "microsoft", "surface", "microsoft surface pro 7"],
      image: "https://m.media-amazon.com/images/I/41XQTF-mheL.SY300_SX300_QL70_FMwebp.jpg",
      price: 749,
    },
    {
      id: 11,
      name: "Apple iPad Pro",
      keywords: ["tablet", "apple", "ipad", "ipad pro"],
      image: "https://m.media-amazon.com/images/I/81pY5hvXfEL._AC_SX522_.jpg",
      price: 999,
    },
    // Add more products as needed
  ];

  const [cart, setCart] = useState([]);

  // Adds a product to the cart or increments the quantity if already present
  const addToCart = (product) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(cart.map(item =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  // Removes a product from the cart or decrements the quantity if more than one
  const removeFromCart = (id) => {
    const item = cart.find((item) => item.id === id);
    if (item.quantity > 1) {
      setCart(cart.map((item) =>
        item.id === id ? { ...item, quantity: item.quantity - 1 } : item
      ));
    } else {
      setCart(cart.filter((item) => item.id !== id));
    }
  };

  // Calculates the total price of all items in the cart
  const calculateTotal = () => {
    return cart.reduce((acc, item) => acc + item.price * item.quantity, 0).toFixed(2);
  };

  // Simulate buying a single item with an alert
  const buyNow = (product) => {
    alert(`Buying ${product.name} for $${product.price}`);
  };

  // Simulate buying all items with an alert, then clears the cart
  const buyAll = () => {
    alert(`Buying all items in the cart for $${calculateTotal()}`);
    setCart([]); // Clears the cart
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center' }}>Products</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {products.map((product) => (
          <div key={product.id} style={{
            border: '1px solid #ddd', borderRadius: '8px', width: '220px', padding: '10px', textAlign: 'center',
            boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
          }}>
            <img src={product.image} alt={product.name} style={{ width: '100%', height: '150px', objectFit: 'cover', borderRadius: '8px' }} />
            <h3 style={{ fontSize: '1.1rem', margin: '10px 0' }}>{product.name}</h3>
            <p style={{ color: '#555' }}>${product.price}</p>
            <button onClick={() => addToCart(product)} style={{
              backgroundColor: '#007bff', color: '#fff', border: 'none', padding: '10px', borderRadius: '5px', cursor: 'pointer'
            }}>
              Add to Cart
            </button>
            <button onClick={() => buyNow(product)} style={{
              backgroundColor: '#28a745', color: '#fff', border: 'none', padding: '10px', borderRadius: '5px', cursor: 'pointer', marginTop: '5px'
            }}>
              Buy Now
            </button>
          </div>
        ))}
      </div>

      <h2 style={{ marginTop: '30px', textAlign: 'center' }}>Cart</h2>
      <div style={{ marginTop: '20px' }}>
        {cart.length > 0 ? (
          <div>
            {cart.map((item) => (
              <div key={item.id} style={{
                display: 'flex', alignItems: 'center', margin: '10px 0', padding: '10px', border: '1px solid #ddd', borderRadius: '8px'
              }}>
                <img src={item.image} alt={item.name} style={{ width: '60px', height: '60px', objectFit: 'cover', borderRadius: '8px' }} />
                <div style={{ marginLeft: '15px', flex: '1' }}>
                  <h4 style={{ margin: '0 0 5px' }}>{item.name}</h4>
                  <p style={{ margin: '0', color: '#555' }}>Price: ${item.price} x {item.quantity}</p>
                  <p style={{ margin: '0', color: '#555' }}>Total: ${(item.price * item.quantity).toFixed(2)}</p>
                </div>
                <button onClick={() => removeFromCart(item.id)} style={{
                  backgroundColor: '#dc3545', color: '#fff', border: 'none', padding: '8px', borderRadius: '5px', cursor: 'pointer'
                }}>
                  Remove
                </button>
              </div>
            ))}
            <div style={{ textAlign: 'center', marginTop: '20px' }}>
              <h3>Total Price: ${calculateTotal()}</h3>
              <button onClick={buyAll} style={{
                backgroundColor: '#ff9800', color: '#fff', border: 'none', padding: '12px 20px', borderRadius: '5px', cursor: 'pointer'
              }}>
                Buy All
              </button>
            </div>
          </div>
        ) : (
          <p style={{ textAlign: 'center', color: '#777' }}>Your cart is empty.</p>
        )}
      </div>
    </div>
  );
}
