import React, { useState } from "react";

const ProductGrid = ({ results, buyProduct, addToCart, user }) => {
  
  const isProductInCart = (product) => {
    if (user && user._id) {
      setCartManage(user.Cart.some((cartItem) => cartItem.Product === product.name));
    }else{
   setCartManage(false);
    }

    return true;
  };

  

  return (
    <React.Fragment>
      <div className="results-grid">
        {results.length > 0 ? (
          results.map((product) => (
            <div key={product.id} className="result-item">
              <img src={product.image} alt="product" className="images" />
              <h3>{product.name}</h3>
              <p>Price: {product.price} â‚¹</p>
              <p>{product.description}</p>
              <div className="cartAndBuy" style={{ display: "flex", gap: "10px" }}>
                <button className="buyButton" onClick={() => buyProduct(product)}>Buy Now</button>
                {isProductInCart(product) && (
                  <button className="cartButton" onClick={() => cartManage ? alert("item already Added") : addToCart(product)}>
                    Add to Cart
                  </button>
                )}
              </div>
            </div>
          ))
        ) : (
          <div className="no-results">No products found</div>
        )}
      </div>
    </React.Fragment>
  );
};

export default ProductGrid;
