import React, { useState, useEffect } from "react";
import products from "../Data/data.js";
import Nav from "./Nav.jsx";
import "../Styles/searchPage.css";
class BTree {
  constructor() {
    this.products = new Map();
  }

  insert(key, product) {
    this.products.set(key, product);
  }

  search(key) {
    return this.products.get(key);
  }
}

// InvertedIndex class to map keywords to product IDs
class InvertedIndex {
  constructor() {
    this.index = new Map();
  }

  add(term, productId) {
    if (!this.index.has(term)) {
      this.index.set(term, new Set());
    }
    this.index.get(term).add(productId);
  }

  search(term) {
    return this.index.get(term) || new Set();
  }
}

// HybridIndex class that combines BTree and InvertedIndex
class HybridIndex {
  constructor() {
    this.bTree = new BTree();
    this.invertedIndex = new InvertedIndex();
    this.productsByName = []; // Store all products for name-based regex search
  }

  addProduct(product) {
    // Add product to B-Tree by ID
    this.bTree.insert(product.id, product);

    // Add product to Inverted Index using keywords
    product.keywords.forEach((keyword) => {
      this.invertedIndex.add(keyword.toLowerCase(), product.id);
    });

    // Store products for regex-based search by name
    this.productsByName.push(product);
  }

  search(query) {
    const lowercaseQuery = query.toLowerCase();

    // Step 1: Attempt to search by product name using regex
    const regex = new RegExp(lowercaseQuery, "i");
    const nameMatches = this.productsByName.filter((product) =>
      regex.test(product.name)
    );

    const keywords = lowercaseQuery.split(" ").filter(Boolean);
    const resultIds = new Set();

    keywords.forEach((keyword) => {
      const ids = this.invertedIndex.search(keyword);
      ids.forEach((id) => resultIds.add(id));
    });

    let result = Array.from(resultIds).map((id) => this.bTree.search(id));
    return nameMatches.length > result.length ? nameMatches : result;
  }
}

// Initialize HybridIndex once outside the component
const hybridIndex = new HybridIndex();
products.forEach((product) => hybridIndex.addProduct(product));

const ProductSearch = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [dropdown, setDropdown] = useState([]);
  const [dropdownStyle, setDropdownStyle] = useState("2px solid #e5e7eb");
  const [pricefilter, setPriceFilter] = useState(10000);

  const handleClear = () => {
    setQuery("");
    const defaultProducts = products.filter((data) => data.default === "yes");
    setResults(defaultProducts);
    setDropdown([]);
    setDropdownStyle("2px solid #e5e7eb");
    setPriceFilter(10000);
  };

  const priceHightoLow = () => {
    let newResult = [...results];
    newResult.sort((a, b) => b.price - a.price);
    setResults(newResult);
    setPriceFilter(newResult[0].price);
  };

  const priceLowtoHigh = () => {
    let newResult = [...results];
    newResult.sort((a, b) => a.price - b.price);
    setResults(newResult);
  };

  const settingPriceRange = (e) => {
    e.preventDefault();
    const searchResults = hybridIndex.search(query);
    let newResult = searchResults.filter(
      (product) => product.price < pricefilter
    );
    setResults(newResult);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (query === " ") {
      return;
    } else {
      const searchResults = hybridIndex.search(query);
      setResults(searchResults);
      setDropdown([]);
      setDropdownStyle("2px solid #e5e7eb");
    }
    console.log(query);
  };

  const searchValue = (e) => {
    const value = e.target.value;
    setQuery(value);
    if (value) {
      const searchResults = hybridIndex.search(value);
      setDropdown(searchResults);
      if (searchResults.length > 0) {
        setDropdownStyle("none");
      } else {
        setDropdownStyle("2px solid #e5e7eb");
      }
    } else {
      setDropdown([]);
    }
  };

  useEffect(() => {
    const defaultProducts = products.filter((data) => data.default === "yes");
    setResults(defaultProducts);
  }, []);

  return (
    <React.Fragment>
      <Nav />
      <div className="search-container">
        <div className="filter">
          <h2>Search for</h2>
          <div className="searchFor">
            <div className="phone" onClick={() => setResults(hybridIndex.search("phone"))}>
              Phones
            </div>
            <div className="laptop" onClick={() => setQuery("laptop")}>
              Laptop
            </div>
            <div className="speaker" onClick={() => setQuery("speaker")}>
              Speaker
            </div>
            <div className="earPhones" onClick={() => setQuery("audio")}>
              Ear Phones
            </div>

            <div className="camara" onClick={() => setQuery("camara")}>
              Camara
            </div>
            <div className="tablet" onClick={() => setQuery("tablet")}>
              Tablet
            </div>
          </div>

          <h2>Sort</h2>
          <div>
            {" "}
            <input
              type="radio"
              name="priceSort"
              id="highToLow"
              value="highToLow"
              onChange={priceHightoLow}
            />
            <label htmlFor="highToLow">High to Low</label>
          </div>

          <div>
            <input
              type="radio"
              name="priceSort"
              id="lowToHigh"
              value="lowToHigh"
              onChange={priceLowtoHigh}
            />
            <label htmlFor="lowToHigh">Low to High</label>
          </div>

          <h2>Price</h2>
          <div>
            <input
              type="range"
              name="priceRange"
              id="priceRange"
              value={pricefilter}
              min={500}
              max={200000}
              onInput={(e) => setPriceFilter(e.target.value)}
            />
            <output>{pricefilter} ₹</output>
            <button onClick={settingPriceRange}>apply</button>
          </div>

          <div>
            <button type="button" className="clear-btn" onClick={handleClear}>
              Clear
            </button>
          </div>
        </div>

        <div className="search-form">
          <form onSubmit={handleSubmit}>
            <div className="input-wrapper">
              <input
                type="text"
                value={query}
                onChange={searchValue}
                placeholder="Search for a product..."
                style={{ borderBottom: `${dropdownStyle}` }}
              />
              <button type="submit">Go</button>
            </div>
            {dropdown.length > 0 && (
              <div className="dropdown">
                {dropdown.map((product) => (
                  <div
                    key={product.id}
                    className="dropDownElement"
                    onClick={() => {
                      setQuery(product.name);
                      setDropdown([]);
                      setResults([product]);
                      setDropdownStyle("2px solid #e5e7eb");
                    }}
                  >
                    {product.name}
                  </div>
                ))}
              </div>
            )}
          </form>

          <div className="results-grid">
            {results.length > 0 ? (
              results.map((product) => (
                <div key={product.id} className="result-item">
                  <img src={product.image} alt="product" className="images" />
                  <h3>{product.name}</h3>
                  <p>Price: {product.price} ₹</p>
                  <p>{product.description}</p>
                  <button className="buyButton">Buy Now</button>
                </div>
              ))
            ) : (
              <div className="no-results">No products found</div>
            )}
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default ProductSearch;
