import React, { useState, useEffect } from "react";
import "../../Styles/buy.css"; // Import custom styles

const Checkout = ({ product }) => {
  const [quantity, setQuantity] = useState(1);
  const [totalPrice, setTotalPrice] = useState(product.price);

  // Calculate total price when quantity changes
  useEffect(() => {
    setTotalPrice(product.price * quantity);
  }, [quantity, product.price]);

  const handleQuantityChange = (event) => {
    const value = parseInt(event.target.value, 10);
    setQuantity(value > 0 ? value : 1); // Minimum quantity is 1
  };

  const handleBuyNow = () => {
    alert(`You have bought ${quantity} of ${product.name} for ₹${totalPrice}`);
    // Here you can add your buy logic, like API calls to process the purchase
  };

  return (
    <div className="buying-page">
      <div className="product-details">
        <img src={product.image} alt={product.name} className="product-image" />
        <div className="product-info">
          <h2>{product.name}</h2>
          <p className="product-description">{product.description}</p>
          <p className="product-price">₹{product.price}</p>
          <p className="offer">Special Offer: Get 10% off on first purchase!</p>
          <div className="quantity-selector">
            <label htmlFor="quantity">Quantity:</label>
            <input
              type="number"
              id="quantity"
              value={quantity}
              onChange={handleQuantityChange}
              min="1"
            />
          </div>
          <p className="total-price">Total Price: ₹{totalPrice}</p>
          <button className="buy-now-button" onClick={handleBuyNow}>
            Buy Now
          </button>
        </div>
      </div>
      <div className="similar-products">
        <h3>Related Products</h3>
        {/* Render similar products, replace below with dynamic data */}
        <div className="similar-product-list">
          {product.relatedProducts.map((relatedProduct) => (
            <div key={relatedProduct.id} className="related-product-item">
              <img src={relatedProduct.image} alt={relatedProduct.name} />
              <p>{relatedProduct.name}</p>
              <p>₹{relatedProduct.price}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Checkout;
